import React, {useState} from 'react';
import axios from 'axios';
import {image} from 'cloudinary-react'; 
 
function AdminPost() {

        const [imageSelected, setImageSelected] = useState("");

        const uploadImage = () => {
            const formData = new FormData()
            formData.append("file", imageSelected)
            formData.append("upload_preset", "tswm04oj")

            axios.post("https://api.cloudinary.com/v1_1/dzhwm1qit/image/upload", formData).then((response) => {
                console.log(response);
            });
        };

        return(
            <div>
                <input type="file" onChange={(event) => {setImageSelected(event.target.files[0])}}/>
                <button onClick={uploadImage}>Upload Image</button>
                <image cloudName="dzhwm1qit" s="https://res.cloudinary.com/dzhwm1qit/image/upload/v1661613292/nkbmexgivtjuhmgcjfwc.jpg" />
            </div>
        );
}
 
export default AdminPost;
