import React from 'react';
import axios from 'axios';

import erasmus from "./logo/img_avatar2.png"
import hems from "./logo/hems.jpg"

import { Link } from 'react-router-dom';
import Footer from "./pageComponent/Footer"; 

class AdminPost extends React.Component {

    constructor(){
        super();
        this.state = {
            selectedFile:'',
            title:'',
            description:''
        }
 
        this.handleInputChange = this.handleInputChange.bind(this);
    }
 
    handleInputChange(event) {
        this.setState({
            selectedFile: event.target.files[0],
          })
    }
 
    submit(){
        const data = new FormData() 
        data.append('file', this.state.selectedFile)
        console.warn(this.state.selectedFile);
        let url = "http://localhost:5000";
 
        axios.post(url, data, {  
        })
        .then(res => { 
            console.warn(res);
        })
 
    }
     
    render(){

        const { email } = this.state

        return(
            
            <div> 
                <div class="center">

                    <form>
                        
                        <h1>Create new Recipe</h1>

                        <div class="txt_field">
                            <input type="text" id="" name="" required/>
                            <span></span>
                            <label >Recipe Name *</label>
                        </div>

                        <div class="w3-center w3-dropdown-hover">

                                <h8>Where does the Recipe come from?  </h8>
                                

                                <select class="drop w3-center" id="country" name="country">
                                <option value="Spain">Spain</option>
                                <option value="Turkey">Turkey</option>
                                <option value="Romania">Romania</option>
                                <option value="Poland">Poland</option>
                                <option value="Germany">Germany</option>
                                </select>
                                
                         </div>

                        <div class="txt_field">
                            <input type="text" id="" name="" required/>
                            <span></span>
                            <label >Preparation Time - ( in min. ) *</label>
                        </div>

                        <textarea value="A brief description of the Recipe:"></textarea>

                    </form>
                </div>

                <Footer></Footer>
            </div>
        );
    };
}
 
export default AdminPost;
