            console.log(e.target.value)
        };

        return(
            <div>

            <form method="post" action="#" id="#">
           
           <div class="form-group files">
             <input type="file" onChange={onInputChange} class="form-control" multiple=""/>
           </div>
            </form>

            <button>Submit</button>

            </div>
        ); 
    }
}

export default AdminPost;
