import React, {userState} from 'react';
import axios from 'axios';
 
class AdminPost extends React.Component{

    render(){

        const uploadImage = (files) => {
            const formData = new FormData()
            formData.append("file", files[0])
            formData.append("upload_preset", "tswm04oj")

            axios.post("https://api.cloudinary.com/v1_1/dzhwm1qit/image/upload")
        };

        return(
            <div>
                <input type="file" onChange={(event) => {uploadImage(event.target.files)}}/>
            </div>
        );
    }
}
 
export default AdminPost;
